import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BookmarkForm } from '../Models/app-edit.interface';

@Component({
  selector: 'app-add-edit',
  templateUrl: './add-edit.component.html',
  styleUrls: ['./add-edit.component.css']
})
export class AddEditComponent {
  id: string;
  model: BookmarkForm;
  submitted = false;

  constructor(private Activatedroute: ActivatedRoute,
    private router:Router) {
    this.id = this.Activatedroute.snapshot.queryParamMap.get('id') || "";
    this.model = new BookmarkForm(18, 'Dr. IQ', 'Really Smart', 'Chuck Overstreet');
  }

  onSubmit() {
    this.submitted = true;
  }
}
<link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>

<div class="container mt-4">
  <h2>Add Bookmark</h2>
  <form [formGroup]="bookmarkForm" (ngSubmit)="onSubmit()">
    <div class="mb-3">
      <label for="name" class="form-label">Name:</label>
      <input type="text" id="name" formControlName="name" class="form-control" required>
    </div>

    <div class="mb-3">
      <label for="url" class="form-label">URL:</label>
      <input type="text" id="url" formControlName="url" class="form-control" required>
    </div>

    <div class="mb-3">
      <label for="category" class="form-label">Category:</label>
      <select id="category" formControlName="category" class="form-select" required>
        <option value="" disabled selected>Select Category</option>
        <option value="category1">Category 1</option>
        <option value="category2">Category 2</option>
      </select>
    </div>

    <div class="mb-3">
      <label for="language" class="form-label">Language:</label>
      <select id="language" formControlName="language" class="form-select" required>
        <option value="" disabled selected>Select Language</option>
        <option value="language1">Language 1</option>
        <option value="language2">Language 2</option>
      </select>
    </div>

    <div class="mb-3">
      <label for="keywords" class="form-label">Keywords (separated by comma):</label>
      <input type="text" id="keywords" formControlName="keywords" class="form-control" required>
    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
